vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Animate coordinates over time
    st.x += sin(iTime * 2.) * 0.3;
    st.y += cos(iTime * 3.) * 0.3;

    // Add noise to create dynamic patterns
    float n1 = noise(st) + 0.1;
    float n2 = noise(vec2(st.x * 1.5, st.y * 1.5)) * 0.5;

    // Create turbulence effect for fractal-like patterns
    float t = turbulent(vec2(st * 3.)) * 4.;

    // Combine noise and turbulence with HSL color space
    vec3 color = hsl2rgb(vec3(
        t,                  // Hue based on turbulence
        n1 * 0.8 + 0.2,   // Saturation using noise
        n2 * 0.6 + 0.4    // Lightness using another noise layer
    ));

    return vec4(color, 1.0);
}
