vec4 Effect2(vec2 uv) {
    // Create pulsating circular pattern
    vec2 st = uv - 0.5;
    float r = length(st);

    // Add time-based pulsation
    float pulse = sin(iTime * 3.0 + r * 10.0) * 0.5 + 0.5;
    pulse += sin(iTime * 2.0 + r * 8.0) * 0.3;

    // Add noise and turbulence
    float noiseVal = noise(uv * 4.0 + iTime);
    noiseVal += turbulent(uv * 16.0 + iTime * 2.0) * 0.5;

    // Combine effects
    float final = r * 3.0 - pulse * 2.0 + noiseVal * 0.4;

    // Convert to color using HSL
    vec3 color = hsl2rgb(vec3(final * 0.6 + iTime * 0.1, 0.7, 0.5));

    return vec4(color, 1.0);
}
