vec4 Effect2(vec2 uv) {
    // Create flowing patterns using polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add time-based animation
    angle += iTime * 0.5;
    radius *= 0.75;

    // Create turbulence effect
    vec3 pos = vec3(uv * 10.0 + iTime, iTime * 0.25);
    float turb = turbulent(pos) * 0.5 + 0.5;

    // Generate color using HSL to RGB conversion
    vec3 hslColor = vec3(
        fract(angle * 0.1 + iTime * 0.2),
        0.7 + turb * 0.3,
        0.8 + sin(radius * 4.0 + iTime) * 0.2
    );

    // Convert to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);
    return vec4(rgbColor, 1.0);
}
