vec4 Effect2(vec2 uv) {
    float t = iTime * 0.1;

    vec2 p = uv;
    p.x *= cos(t);
    p.y *= sin(t);

    p += noise(p * 5.0);
    p += turbulent(vec2(noise(p * 3.0), noise(p * 4.0)));

    float a = atan2(p.y, p.x) + t;
    vec3 color = hsl2rgb(vec3(sin(a) * 0.5 + 0.5, 
                              cos(a) * 0.5 + 0.5,
                              sin(t) * 0.5 + 0.5));

    return vec4(color, 1.0);
}
