vec4 Effect2(vec2 uv) {
    vec2 pos = uv * 2.0 - 1.0;
    float speed = 0.5;

    float time = iTime * speed;
    vec2 trapCenter = vec2(sin(time * 0.7), cos(time * 0.6)) * 0.3;

    float maxIterations = 30.0;
    float escapeRadius = 1.5;
    float distanceSum = 0.0;

    for(float i = 0.0; i < maxIterations; i++) {
        pos = vec2(
            pow(length(pos), 2.0) * cos(atan2(pos.y, pos.x)) - trapCenter.x,
            pow(length(pos), 2.0) * sin(atan2(pos.y, pos.x)) + trapCenter.y
        );

        float noiseFactor = noise(vec3(pos * 0.5 + time, 0.0)) * 0.1;
        pos += vec2(sin(time + i) * noiseFactor, cos(time + i) * noiseFactor);

        if(length(pos - trapCenter) < 0.2) {
            distanceSum += length(pos) * 0.1;
        }
    }

    float normalizedDistance = min(distanceSum / maxIterations, 1.0);
    vec3 color = hsl2rgb(vec3(
        (time + pos.x * 0.5) * 4.0,
        0.7,
        0.5 + normalizedDistance * 0.5
    ));

    return vec4(color, 1.0);
}
