vec4 Effect2(vec2 uv) {
    // Scale and shift UV coordinates
    vec2 st = uv * 5.0 - 2.0;

    // Polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add some movement over time
    theta += sin(iTime) * 0.5;

    // Create orbit trap effect using turbulence
    float trap = turbulent(vec3(r, theta, iTime));

    // Convert back to cartesian coordinates
    vec2 pos = vec2(
        cos(theta + trap * 0.5) * r,
        sin(theta - trap * 0.5) * r
    );

    // Add noise and color
    float n = noise(pos * 0.5 + iTime);
    vec3 color = hsl2rgb(vec3(
        theta * 0.15 + n * 0.2,
        0.7 + trap * 0.3,
        0.5 + sin(iTime) * 0.2
    ));

    return vec4(color, 1.0);
}
