vec4 Effect2(vec2 uv) {
    // Center and scale coordinates
    vec2 st = uv - 0.5;
    st *= 3.0; // Scale up for better detail

    // Base color with time-based hue shift
    vec3 baseColor = hsl2rgb(vec3(0.5 + iTime * 0.1, 0.7, 0.6));

    // Create a turbulent pattern
    float turb = turbulent(vec3(st.x*0.5, st.y*0.5, iTime*0.2)) * 0.5 + 0.5;

    // Add radial component
    float dist = length(st);
    float radial = (1.0 - smoothstep(0.0, 1.2, dist)) * 0.7;
    radial += noise(vec3(dist*4.0, iTime*0.2, 0.0)) * 0.3;

    // Combine patterns
    float pattern = turb * (radial + 0.3);
    pattern = mix(radial, turb, pattern) * 1.5;

    // Color transformation
    vec3 color = hsv2rgb(vec3(0.6 + iTime*0.1, pattern*0.8 + 0.2, pattern*0.7 + 0.3));

    // Add distance-based intensity
    float distFactor = (1.0 - smoothstep(0.0, 1.5, dist)) * 0.6;
    color *= distFactor + 0.4;

    // Final pulsation effect
    color *= sin(iTime*0.3 + pattern*2.0) * 0.5 + 0.7;

    return vec4(color * 1.2, 1.0);
}
