vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 st = uv - 0.5;

    // Convert to polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Create rotating pattern using time
    float time = iTime * 0.5; // Speed control

    // Create multiple mirrored sections
    int numSections = 6;
    theta = mod(theta + time, (2.0 * PI) / float(numSections)) * float(numSections);

    // Calculate color based on angle and radius
    vec3 color = hsl2rgb(vec3(
        theta * 180.0 / PI * 0.5, // Hue based on angle
        0.7 + 0.3 * sin(r * 10.0 + time), // Saturation variation
        0.5 + 0.5 * sin(r * 20.0 + theta * 4.0) // Lightness variation
    ));

    // Add noise for extra detail
    float noiseValue = turbulent(vec3(uv * 10.0, time));
    color *= 1.0 + 0.2 * noiseValue;

    return vec4(color, 1.0);
}
