vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan2(pos.y, pos.x);
    // Create swirling effect with time-based rotation
    theta += iTime * 0.5;

    // Add turbulence to create dynamic patterns
    vec3 noisePos = vec3(uv * 10.0 + iTime, 0.0);
    float turb = turbulent(noisePos) * 0.5 + 0.5;

    // Create expanding rings using sine waves
    float ring = sin(r * 8.0 + iTime * 2.0) * 0.5 + 0.5;

    // Combine effects and create color transitions
    vec3 color = hsl(
        (theta / (2.0 * PI)) * 360.0,   // Hue based on angle
        0.7 + turb * 0.3,              // Saturation with turbulence
        0.5 + ring * 0.5               // Lightness with expanding rings
    );

    return vec4(color, 1.0);
}
