vec4 Effect2(vec2 uv) {
    // Base color with time-based hue
    vec3 baseColor = hsl2rgb(vec3(0.5 + 0.1*sin(iTime*0.5), 1.0, 0.5));

    // Create radial gradient from center
    vec2 coord = uv * 2.0 - 1.0;
    float dist = length(coord);
    float radius = 1.0 - smoothstep(0.0, 0.8, dist);

    // Add fractal noise for detail
    vec3 noiseScale = vec3(4.0, 8.0, 16.0);
    float noise1 = turbulent(coord * noiseScale.xy + iTime) * 0.5;
    float noise2 = turbulent(vec3(coord * noiseScale.zy, iTime)) * 0.3;

    // Combine noise and radial gradient
    float detail = (noise1 + noise2) * radius;

    // Create pulsating effect
    float pulse = sin(iTime * 0.5) * 0.5 + 0.5;
    detail *= mix(1.0, 1.5, pulse);

    // Add lighting effect
    vec3 light = vec3(1.0 - dist * 0.8) * (noise1 + noise2) * 0.5;
    light += vec3(noise1 * 0.3);

    // Combine all elements
    vec3 finalColor = baseColor * radius * (detail + light);

    return vec4(finalColor, 1.0);
}
