vec4 Effect2(vec2 uv) {
    float time = iTime * 0.1;

    // Create a warping effect using multiple frequencies and noise
    vec2 st = uv;

    // Add time-based distortion
    float freq = length(sin(time));
    st.x += sin(st.y * 3.14 + time) * 0.5;
    st.y += cos(st.x * 3.14 + time * 0.5) * 0.5;

    // Add more complex warping using noise
    float noiseVal = noise(vec2(sin(st.x), cos(st.y)));

    vec4 result = vec4(
        sin(st.x * 3.14 + time) * 0.5,
        cos(st.y * 3.14 + time * 0.5) * 0.5,
        noiseVal,
        1.0
    );

    return result;
}
