vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for hexagonal patterns
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 6.0; // Add time-based rotation

    // Create hexagonal tiling pattern using polar coordinates
    a += sin(r * 5.0 + iTime) * 0.5;
    vec2 q = vec2(r, a);

    // Use noise and turbulence for dynamic patterns
    float noiseVal = noise(vec3(q.x, q.y, iTime)) * 0.5 + 0.5;
    float turbulentVal = turbulent(vec2(q.x * 10.0, q.y));

    // Create pulsating color effect using HSL
    vec3 color = hsl(
        sin(iTime) * 0.5 + 0.5,   // Hue variation over time
        0.7 + noiseVal * 0.3,     // Saturation with noise modulation
        0.5 + sin(iTime * 2.0) * 0.5 + turbulentVal * 0.3  // Pulsating lightness
    );

    // Combine all elements for final output
    vec4 fragColor = vec4(color, 1.0);
    return fragColor;
}
