vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);
    // Create concentric circles using sine waves
    float circle = sin(r * 10.0 + iTime * 2.0) * 0.5 + 0.5;
    circle += sin(r * 30.0 + iTime * 4.0) * 0.2;
    // Create RGB shifts that rotate over time
    float timeAngle = iTime * 0.1;
    vec3 colorShifts = vec3(
        sin(timeAngle + 0.0) * 0.5 + 0.5,
        sin(timeAngle + 2.094) * 0.5 + 0.5, // 2pi/3
        sin(timeAngle + 4.188) * 0.5 + 0.5  // 4pi/3
    );
    // Combine circles with color shifts
    vec3 finalColor = mix(vec3(0.0), colorShifts, circle);
    return vec4(finalColor, 1.0);
}
