vec4 Effect2(vec2 uv) {
    vec4 fragColor;

    // Create time-varying position
    vec2 pos = uv * 5.0 + sin(iTime) * 10.0;

    // Generate three overlapping sine wave layers with different frequencies
    float wave1 = sin(pos.x * 3.0 + iTime) * 0.5 + 0.5;
    float wave2 = sin(pos.y * 4.0 - iTime * 2.0) * 0.5 + 0.5;
    float wave3 = sin((pos.x + pos.y) * 2.0 + iTime * 3.0) * 0.5 + 0.5;

    // Combine waves and add turbulence
    float combined = (wave1 + wave2 + wave3) / 3.0;
    combined += turbulent(uv * 4.0 + vec2(iTime)) * 0.3;

    // Create color cycling effect using HSL to RGB conversion
    vec3 baseColor = hsl2rgb(vec3(
        sin(iTime * 0.5) * 0.5 + 0.5,   // Hue variation over time
        1.0,                             // Saturation
        0.7                              // Lightness
    ));

    // Mix colors with noise and waves
    vec3 finalColor = mix(
        baseColor,
        vec3(1.0),                       // White for highlights
        combined * 0.5 + 0.5             // Combine wave pattern with color cycling
    );

    fragColor = vec4(finalColor, 1.0);
    return fragColor;
}
