vec4 Effect2(vec2 uv) {
    // Create base pattern using sine waves
    vec2 st = uv * 10.0;
    float time = iTime * 0.5;

    // Flowing sine wave patterns
    float patternX = sin(st.x + time) * 0.5 + 0.5;
    float patternY = sin(st.y + time * 0.7) * 0.5 + 0.5;

    // Create rotating color wheel effect
    vec2 polar = vec2(
        length(st - 5.0),
        atan2(st.y - 5.0, st.x - 5.0)
    );
    float angle = polar.y + time;

    // Combine patterns and colors
    vec3 color1 = hsl(patternX * 0.7 + 0.3, 0.8, 0.6);
    vec3 color2 = hsl((patternY + sin(angle) * 0.5 + 0.5) * 0.7 + 0.3, 0.8, 0.6);

    // Add turbulence for extra detail
    vec2 noiseUV = uv * 10.0;
    float turb = turbulent(noiseUV) * 0.3;

    // Combine everything and output
    return vec4(
        (color1 + color2) * (1.0 + turb * 0.5),
        1.0
    );
}
