vec4 Effect2(vec2 uv) {
    // Center coordinates and scale
    vec2 st = (uv - 0.5) * 3.0;

    // Polar coordinates
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Pulsating sine waves with multiple frequencies
    float wave1 = sin(a * 5.0 + iTime * 2.0) * 0.5;
    float wave2 = sin(a * 7.0 - iTime * 3.0) * 0.4;
    float wave3 = sin(r * 8.0 + iTime * 1.5) * 0.3;

    // Combine waves with time modulation
    float value = (wave1 + wave2 + wave3) * 
                  sin(r * 2.0 + iTime) * 0.5 + 0.5;

    // RGB shifts based on angle and radius
    vec3 color = vec3(
        sin(a + r * 0.5 + iTime * 0.7),
        sin(a + r * 0.5 + iTime * 0.7 + 2.094),  // 2pi/3
        sin(a + r * 0.5 + iTime * 0.7 + 4.188)   // 4pi/3
    );

    // Add turbulence for more organic feel
    float turb = turbulent(vec2(st.x*0.5, st.y*0.5)) * 
                 turbulent(vec2(iTime*0.2));

    // Combine everything and scale output
    vec3 finalColor = (color + turb) * value * 1.5;

    return vec4(finalColor, 1.0);
}
