vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float theta = atan(pos.y, pos.x);
    // Create gear shape using sine wave pattern
    float gear = sin(theta * 16.0) * 0.5 + 0.5;
    gear = smoothstep(0.4, 0.6, gear);

    // Animate rotation and scale
    float time = iTime * 0.2;
    float rot1 = time;
    float rot2 = -time + theta * 3.0;
    // Create two interlocking gears with different sizes and rotations
    vec2 p1 = vec2(cos(rot1) * r, sin(rot1) * r);
    vec2 p2 = vec2(cos(rot2) * (r * 0.7), sin(rot2) * (r * 0.7));

    // Add turbulence for dynamic effect
    float turb = turbulent(vec3(p1 * 5.0, iTime)) * 0.3;
    gear += turb;
    // Color cycling using HSL
    vec3 color1 = hsl2rgb(vec3((iTime + theta) * 0.1, 1.0, 0.8));
    vec3 color2 = hsl2rgb(vec3((iTime + theta + PI) * 0.1, 1.0, 0.6));
    // Combine gears with mix
    float gearA = smoothstep(0.4, 0.5, r);
    float gearB = smoothstep(0.7, 0.8, r);
    vec3 finalColor = mix(color1, color2, gearB) * (gearA + gearB);
    return vec4(finalColor, 1.0);
}
