vec4 Effect2(vec2 uv) {
    // Create a time-varying base frequency
    float time = iTime * 0.5;

    // Generate sine wave patterns with different frequencies
    float v1 = sin(uv.x * 8.0 + time) * 0.5 + 0.5;
    float v2 = sin(uv.y * 4.0 + time * 0.7) * 0.3 + 0.3;

    // Combine with noise for added complexity
    vec2 noiseUV = uv * 10.0 + vec2(time, time);
    float noiseVal = noise(noiseUV) * 0.5 + 0.5;

    // Mix the patterns together
    float value = v1 + v2 + noiseVal;
    value = mod(value, 1.0); // Keep within [0,1]

    // Create shifting hues based on position and time
    vec3 color = hsl2rgb(vec3(
        uv.x * 5.0 + time,   // Hue varies across screen and time
        0.7,                // Saturation
        value               // Lightness varies with the wave pattern
    ));

    return vec4(color, 1.0);
}
