vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating hexagon pattern using polar coordinates
    vec2 st = uv * 10.0; // Scale up for more hexagons
    float theta = atan2(st.y, st.x) + time; // Rotate over time
    float r = length(st);

    // Hexagonal tiling pattern
    float pattern = sin(theta * 6.0) * 0.5 + 0.5;
    pattern = fract(pattern + sin(r * 0.1 + time));

    // Color shifting using HSL
    vec3 color = hsl2rgb(vec3(
        fract(time * 0.2 + st.x * 0.1),
        0.7,
        0.5 + 0.5 * sin(r * 0.5 + time)
    ));

    // Add turbulence for dynamic effect
    float noise = turbulent(uv * 10.0 + time);
    color *= 1.0 + noise * 0.3;

    return vec4(color, 1.0);
}
