vec4 Effect2(vec2 uv) {
    // Create undulating pattern using sine waves with time offset
    float wave = sin(uv.x * 5.0 + iTime) * 0.5;
    wave += sin(uv.y * 3.0 + iTime * 0.7 + 1.5708) * 0.5; // Add another perpendicular wave

    // Mix in some turbulence for organic movement
    float turb = turbulent(uv * 4.0 + vec2(iTime));

    // Combine waves and turbulence to create shifting pattern
    float pattern = (wave + turb) * 0.5 + 0.5;

    // Create rainbow effect by cycling through hues
    vec3 color = hsl2rgb(vec3(
        fract(pattern + iTime / 2.0) * 360.0,   // Hue that shifts over time
        1.0,                                    // Full saturation
        0.5                                     // Mid lightness
    ));

    return vec4(color, 1.0);
}
