vec4 Effect2(vec2 uv) {
    // Center the coordinates and normalize by aspect ratio
    vec2 centerUV = (uv - 0.5) / max(uv.x, uv.y);

    // Calculate distance from center
    float dist = length(centerUV);

    // Create expanding wave effect using time
    float radius = mod(dist + iTime * 0.1, 1.0);

    // Convert distance to hue value (0-1 range)
    vec3 colorHSL = vec3(radius, 1.0, 0.5); // HSL: hue, saturation, lightness

    // Convert HSL to RGB and return
    return vec4(hsl2rgb(colorHSL), 1.0);
}
