vec4 Effect2(vec2 uv) {
    // Create a radial gradient from center
    vec2 toCenter = uv - 0.5;
    float dist = length(toCenter);
    vec2 dir = normalize(toCenter);

    // Add sine wave distortion based on distance and time
    float wave = sin(dist * 10.0 + iTime) * 0.3;
    float wave2 = sin(iTime + uv.x * 5.0) * 0.2;

    // Create a rotating pattern using polar coordinates
    vec2 polar = vec2(
        dist * (dir.x + sin(iTime + dir.y * 10.0)),
        dist * (dir.y + cos(iTime + dir.x * 10.0))
    );

    // Add turbulence for more complex patterns
    float turb = turbulent(polar * 5.0) * 0.3;

    // Combine all components into final color
    vec3 color = hsl2rgb(vec3(
        (dist + wave + turb) * 180.0,
        0.7 + sin(iTime) * 0.3,
        0.5 + cos(dist * 10.0 + iTime) * 0.5
    ));

    return vec4(color, 1.0);
}
