vec4 Effect2(vec2 uv) {
    // Create base displacement using sine waves
    float displacement = sin(uv.x * 20.0 + iTime) * 0.5;
    displacement += sin(uv.y * 10.0 + iTime * 0.5) * 0.3;

    // Add time-based color variation
    vec3 color = hsl(
        mod(iTime * 0.2 + displacement, 1.0),  // Hue
        0.7,                                    // Saturation
        0.6                                     // Lightness
    );

    // Create secondary displacement for layered effect
    float displacement2 = cos(uv.x * 15.0 - iTime) * 0.4;
    displacement2 += sin(uv.y * 8.0 + iTime * 0.3) * 0.2;

    // Combine displacements and create color variation
    float combinedDisplacement = (displacement + displacement2) * 0.5;
    vec3 finalColor = hsl2rgb(vec3(
        mod(iTime * 0.1 + combinedDisplacement, 1.0),  // Hue
        0.8,                                           // Saturation
        0.7 + turbulent(uv * 10.0 + iTime) * 0.1       // Lightness with turbulence
    ));

    // Add noise and time-based transformations for more detail
    float noiseValue = noise(uv * 20.0 + vec2(iTime));
    finalColor += hsv2rgb(vec3(
        mod(iTime * 0.15, 1.0),   // Hue
        0.6,                      // Saturation
        noiseValue * 0.2          // Value with noise modulation
    ));

    return vec4(finalColor * (1.0 + sin(iTime) * 0.3), 1.0);
}
