vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    float a = atan2(uv.y, uv.x);
    float r = length(uv);

    // Create rotating sine wave pattern
    a += sin(iTime * 0.5) * 3.0;
    vec2 st = vec2(sin(a + iTime), cos(r * 10.0 + iTime));

    // Add noise and turbulence
    float noiseVal = noise(st * 4.0 + iTime);
    noiseVal += turbulent(st * 2.0 + iTime) * 0.5;

    // Create color shifts using HSL
    vec3 color = hsl(a * 0.1, 0.7, 0.5);
    color.r += sin(iTime * 0.4 + r * 2.0) * 0.5;
    color.g += cos(iTime * 0.6 + r * 3.0) * 0.5;
    color.b += sin(iTime * 0.8 + r * 4.0) * 0.5;

    // Combine noise with color
    vec3 finalColor = mix(color, vec3(noiseVal), 0.3);

    return vec4(finalColor, 1.0);
}
