vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Twirling motion
    float time = iTime * 0.5;
    float angle = mod(time, PI);
    mat2 rotation = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    st = rotation * (st * 1.5) + vec2(0.5, 0.5);

    // Morphing noise effect
    vec2 pos = st;
    float n = noise(pos * time);

    // Color transformation and shading
    vec3 color = hsl2rgb(vec3(
        mod(time * 4.0 + n, PI),  // Twirling hue
        0.8,                       // High saturation
        0.8                        // Bright value
    ));

    // Add specular highlight
    vec3 light = normalize(vec3(1.0, 1.0, 1.0));
    float spec = pow(max(dot(normalize(vec3(pos, 1.0)), light), 0.0), 4.0);
    color += vec3(1.0) * spec;

    return vec4(color, 1.0);
}
