vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 centered = uv - 0.5;
    float radius = length(centered);
    float angle = atan2(centered.y, centered.x);

    // Add time-based rotation and expansion
    angle += iTime * 0.1;
    radius += sin(iTime) * 0.3 + 0.7;

    // Create multiple rotating wheels using a grid pattern
    vec2 grid = fract(uv * 8.0);
    float wheelRadius = length(grid - 0.5) * 0.6;

    // Combine rotation and expansion for final color transformation
    float hue = angle * 0.1 + radius * 0.2 + iTime * 0.3;
    vec3 color = hsl2rgb(vec3(hue, 0.7, 0.5));

    // Add subtle gradients using turbulence
    float noiseValue = turbulent(uv * 4.0 + iTime);
    color *= mix(1.0, 1.2, noiseValue);

    return vec4(color, 1.0);
}
