vec4 Effect2(vec2 uv) {
    // Create a grid of points
    vec2 st = fract(uv * 50.0);

    // Calculate distance to nearest point
    float d = length(st - 0.5);

    // Add some movement using sine waves
    vec2 g = floor(uv * 50.0);
    g.y += sin(iTime + g.x) * 0.5;
    float m = length(fract(g) - uv);

    // Create color transition from red to blue
    vec3 col = mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 0.0, 1.0), (sin(iTime) + 1.0)/2.0);

    // Add some noise for variation
    float n = turbulent(uv * 10.0 + iTime);

    // Create the final effect with glow
    vec3 finalCol = mix(vec3(0.0), col, smoothstep(0.05, 0.0, d + n));

    return vec4(finalCol, 1.0);
}
