vec4 Effect2(vec2 uv) {
    // Create flowing sine wave patterns
    float time = iTime * 0.5;

    // Moving sine waves in both directions
    vec2 st = uv * 10.0 + vec2(time, -time);
    float sines = sin(st.x) * sin(st.y + time) * 0.5 + 0.5;

    // Rotating color palette using HSL
    float hue = 0.5 + sin(iTime * 0.3) / 4.0;
    vec3 color = hsl2rgb(vec3(hue, 1.0, sines));

    // Add noise for texture
    vec2 noisePos = vec2(sin(time), uv.y);
    float n = noise(noisePos * 5.0 + time) * 0.2;

    // Combine elements
    color *= 1.0 - abs(n);
    color += sin(sines * pi + time) * 0.3;

    return vec4(color, 1.0);
}
