vec4 Effect2(vec2 uv) {
    // Center and rotate coordinates
    vec2 st = uv * 2.0 - 1.0;
    st *= mat2(cos(3.0 * iTime), sin(3.0 * iTime), 
               -sin(3.0 * iTime), cos(3.0 * iTime));

    // Polar coordinates
    float theta = atan2(st.y, st.x);
    float r = length(st) * 5.0;

    // Rotating triangle pattern
    theta += 3.0 * iTime;
    vec3 tri = vec3(
        mod(theta + 120.0, 360.0),
        mod(theta + 240.0, 360.0),
        mod(theta, 360.0)
    ) / 360.0;

    // Expanding rings
    float ring = sin(r - iTime * 2.0) * 0.5 + 0.5;
    ring += sin(r * 2.0 - iTime * 4.0) * 0.5;

    // Color transformation using HSL
    vec3 color = hsl(
        mod(theta / 60.0 + iTime, 1.0),
        0.7,
        ring * 0.8 + 0.2
    );

    // Add turbulence for extra detail
    float turb = turbulent(vec3(st.x, st.y, iTime)) * 0.5;
    color *= 1.0 + turb * 0.5;

    return vec4(color, 1.0);
}
