vec4 Effect2(vec2 uv) {
    // Create a position variable centered at 0.0
    vec2 pos = uv * 2.0 - 1.0;

    // Add time-based movement
    float t = iTime * 0.5;
    vec2 st = pos + vec2(sin(t + pos.x) * 0.3, cos(t + pos.y) * 0.3);

    // Create a noise-based pattern with turbulence
    float n = noise(st * 4.0 + t) * 0.5 + 0.5;
    n += turbulent(st * 2.0 + t) * 0.3;

    // Convert to HSL color space and back to RGB
    vec3 color = hsl2rgb(vec3(
        (n * 0.6 + 0.4) * 360.0,   // Hue
        1.0 - abs(n - 0.5) * 2.0,  // Saturation
        n                          // Lightness
    ));

    return vec4(color, 1.0);
}
