vec4 Effect2(vec2 uv) {
    // Create rotating checkerboard pattern
    float time = iTime * 0.5;

    // Add some turbulence to the UV coordinates
    vec2 distortedUV = uv + turbulent(uv * 10.0 + time) * 0.1;

    // Rotate the coordinates
    float angle = time;
    float cosTheta = cos(angle);
    float sinTheta = sin(angle);
    mat2 rotationMatrix = mat2(cosTheta, -sinTheta, sinTheta, cosTheta);
    vec2 rotatedUV = (distortedUV - 0.5) * rotationMatrix + 0.5;

    // Create checkerboard pattern
    vec2 grid = floor(rotatedUV * 10.0);
    float check = fract(sin(grid.x * 123.456 + grid.y * 789.123) * time);

    // Generate shifting hues using HSL to RGB conversion
    vec3 colorBase = hsl2rgb(vec3(fract(time * 0.2 + grid.x * 0.1 + grid.y * 0.2), 0.6, 0.5));
    vec3 finalColor = mix(colorBase, vec3(1.0), check * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
