vec4 Effect2(vec2 uv) {
    vec3 finalColor = vec3(0.0);

    // Create multiple layers of particles
    for (float i = 0.0; i < 5.0; i++) {
        // Offset coordinates for each layer
        vec2 pos = uv * 10.0 + vec2(i, i) * 0.5;

        // Add turbulent noise to create dynamic movement
        float noiseVal = turbulent(vec3(pos, iTime));

        // Create color variations using HSL
        vec3 hslColor = vec3(
            fract(noiseVal + iTime * 0.2),
            0.8,
            0.5 + abs(sin(iTime * 0.3 + noiseVal)) * 0.5
        );

        // Convert to RGB and add to final color with alpha blending
        vec3 rgbColor = hsl2rgb(hslColor);
        finalColor += mix(finalColor, rgbColor, 0.1);
    }

    return vec4(finalColor, 1.0);
}
