vec4 Effect2(vec2 uv) {
    // Create base color with noise and time
    vec3 baseColor = hsl2rgb(vec3(noise(uv * 10.0 + iTime), 0.6, 0.5));

    // Add rotational gradient effect
    float angle = atan2(sin(iTime), cos(iTime)) + length(uv - 0.5);
    vec3 gradientColor = hsl2rgb(vec3(angle * 180.0 / PI, 0.7, 0.6));

    // Add turbulence for flowing effect
    float turb = turbulent(vec3(uv * 4.0, iTime) * 2.0);
    baseColor += gradientColor * (turb * 0.5 + 0.5);

    // Create expanding circles pattern
    vec2 center = uv - 0.5;
    float dist = length(center);
    float timeScale = iTime * 0.1;
    float circle = step(sin(timeScale) * 0.5 + 0.5, dist);

    // Combine all elements and output
    return vec4(baseColor * (circle * 0.3 + 0.7), 1.0);
}
