vec4 Effect2(vec2 uv) {
    // Create flowing movement vectors using noise
    vec3 flow1 = vec3(uv * 0.5 + iTime, 0.0);
    vec3 flow2 = vec3(uv * -0.5 + iTime * 0.7, 1.0);

    // Generate turbulent patterns for both flows
    float turb1 = turbulent(flow1 * 0.5);
    float turb2 = turbulent(flow2 * 0.5);

    // Combine turbulence effects with UV coordinates
    vec2 distortedUV = uv + (turb1 - 0.5) * 0.3 + (turb2 - 0.5) * 0.2;

    // Create color transitions using HSL to RGB conversion
    float hue = iTime * 0.2 + length(distortedUV) * 0.5;
    vec3 baseColor = hsl2rgb(vec3(hue, 1.0, 0.6));

    // Add depth variation using noise
    float height = noise(vec3(uv * 0.5 + iTime, 0.0)) * 0.5 + 0.5;

    // Mix colors and add value variation
    vec3 finalColor = mix(baseColor, hsl2rgb(vec3(hue + sin(iTime)*0.5, 1.0, 0.4)), height);
    finalColor *= 0.7 + noise(vec3(uv * 0.2 + iTime*0.3, 0.0)) * 0.3;

    return vec4(finalColor, 1.0);
}
