vec4 Effect2(vec2 uv) {
    // Scale and tile the UV coordinates
    vec2 st = fract(uv * 100.0);

    // Create a base noise pattern with turbulence
    float n = noise(st + iTime);
    float t = turbulent(st * 5.0 + iTime);

    // Combine noise patterns with some math operations
    float pattern = sin(n * pi) * cos(t * pi) + 
                   sin((st.x + st.y) * pi) * 0.5;

    // Create a color from HSL values
    vec3 color = hsl2rgb(vec3(
        fract(pattern + iTime * 0.1),   // Hue
        0.7,                            // Saturation
        0.5 + sin(iTime) * 0.2         // Lightness with time variation
    ));

    // Add some turbulence to the color for more dynamic effect
    vec3 finalColor = color * (1.0 + turbulent(vec3(st.x, st.y, iTime)) * 0.3);

    return vec4(finalColor, 1.0);
}
