vec4 Effect2(vec2 uv) {
    // Rotate coordinates around center
    vec2 rotated = uv - 0.5;
    float angle = iTime * 0.5; // Rotation speed
    rotated = vec2(
        rotated.x * cos(angle) - rotated.y * sin(angle),
        rotated.x * sin(angle) + rotated.y * cos(angle)
    );
    rotated += 0.5;
    // Checkerboard grid pattern
    float scale = 8.0;
    float x = abs(fract(rotated.x * scale) - 0.5);
    float y = abs(fract(rotated.y * scale) - 0.5);

    // Combine grid lines
    float grid = smoothstep(0.0, 1.0, min(x, y));
    // Color transition based on distance from center
    vec2 centerDist = uv - 0.5;
    float dist = length(centerDist);
    vec3 color = vec3(
        sin(dist * 8.0 + iTime) * 0.5 + 0.5,
        cos(dist * 8.0 + iTime + 2.094) * 0.5 + 0.5, // 120 degrees offset
        cos(dist * 8.0 + iTime - 2.094) * 0.5 + 0.5  // 120 degrees offset
    );
    // Mix colors with grid pattern
    vec3 finalColor = mix(vec3(1.0), color, grid);
    return vec4(finalColor, 1.0);
}
