vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Transform coordinates with time for animation
    float scale = 1.5 + sin(iTime * 0.5) * 0.5;
    vec2 stTransformed = (st * scale) * 3.0;

    // Add rotation based on time
    float angle = iTime * 4.0;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    stTransformed *= rot;

    // Use noise texture with transformed coordinates
    vec2 noiseUV = (stTransformed + uv) * 0.5;
    vec3 noiseColor = hsl2rgb(hsv2rgb(vec3(noise(uv * 10.0), 0.8, 0.7)));

    // Add turbulence for detail
    float t = turbulent(stTransformed * 4.0);

    // Combine and colorize the effect
    vec3 finalColor = hsv2rgb(vec3(
        (noiseColor.r + noiseColor.g + noiseColor.b) / 3.0,
        0.8,
        0.9 + sin(t * 5.0 + iTime) * 0.1
    ));

    return vec4(finalColor, 1.0);
}
