vec4 Effect2(vec2 uv) {
    // Calculate distance from center and angle
    float d = length(uv);
    float a = atan2(uv.y, uv.x) / (2.0 * pi) + 0.5;

    // Create pulsing circular pattern
    float radius = 0.5 + sin(iTime * 0.5) * 0.3;

    // Generate noise texture
    float n = noise(vec3(uv, iTime));

    // Color transformations using HSL to RGB conversion
    vec3 color1 = hsl2rgb(vec3(0.6 + a * 0.4, 0.8, 0.7));
    vec3 color2 = hsl2rgb(vec3(0.3 + a * 0.5, 0.7, 0.8));

    // Combine colors and noise
    vec3 finalColor;
    if (d < radius) {
        finalColor = mix(color1, color2, n);
    } else {
        finalColor = mix(vec3(0.1), color1 * 0.5, n);
    }

    return vec4(finalColor, 1.0);
}
