vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates centered at (0.5, 0.5)
    vec2 centeredUV = uv - 0.5;
    float radius = length(centeredUV);

    // Add pulsation effect using time
    radius += sin(iTime * 2.0) * 0.5;

    // Calculate angle for color variation
    float angle = atan2(centeredUV.y, centeredUV.x);

    // Create color based on distance from center and time
    vec3 hsvColor = vec3(
        mod(radius * 5.0 + iTime, 1.0),  // Hue varies with radius and time
        0.8,                              // Saturation
        sin(radius * 2.0) * 0.5 + 0.5     // Value varies with radius
    );

    return vec4(hsv2rgb(hsvColor), 1.0);
}
