vec4 Effect2(vec2 uv) {
    // Create a time-based animation component
    float time = iTime * 0.5;

    // Generate base noise pattern
    vec2 st = uv * 10.0;
    float baseNoise = noise(st);

    // Add turbulence for more dynamic effect
    float turb = turbulent(st * 0.5) * 0.5 + 0.5;

    // Create flowing color transitions
    vec3 color = hsl2rgb(vec3(
        (baseNoise + time) * 0.4 + 0.1,
        sin(time * 0.7) * 0.5 + 0.5,
        cos(time * 0.6) * 0.5 + 0.5
    ));

    // Add movement and distortion to the pattern
    vec2 velocity = vec2(
        sin(time + baseNoise * 10.0),
        cos(time + baseNoise * 10.0)
    ) * 0.05;

    // Combine all elements for final effect
    vec4 fragColor = vec4(color, 1.0);
    fragColor.rgb *= mix(0.8, 1.2, turb);
    fragColor.rgb += velocity.x * 0.3;

    return fragColor;
}
