vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0);

    // Animated vertical position for scanline effect
    float scanPos = sin(iTime * 5.0) * 0.5 + 0.5;

    // Create horizontal sweeping lines
    float lineSpeed = 3.0;
    uv.y += fract(uv.x * lineSpeed + iTime * 2.0);

    // Add scanline influence to noise generation
    vec2 noiseUV = uv * 10.0 + vec2(0.0, scanPos * 5.0);
    float noiseValue = noise(noiseUV);

    // Animate color using HSL values
    float hue = iTime * 0.5;
    float saturation = 0.8;
    vec3 hslColor = hsl2rgb(vec3(hue, saturation, 0.5 + sin(iTime) * 0.2));

    // Apply noise-based color shift
    vec3 finalColor = mix(hslColor, hslColor * (noiseValue + 1.0), 0.7);

    return vec4(finalColor, 1.0);
}
