vec4 Effect2(vec2 uv) {
    vec2 st = uv * iTime;

    // Get noise value from texture
    float n = noise(st * 8.0);

    // Create color variations using HSV
    vec3 color1 = hsv2rgb(vec3(
        mod(iTime * 0.5, 1.0), 
        0.7 + sin(iTime) * 0.3, 
        0.6 + n * 0.4
    ));

    // Add secondary color influence with time offset
    vec3 color2 = hsv2rgb(vec3(
        mod(iTime * 0.5 + 1.0, 1.0), 
        0.7 + sin(iTime + PI) * 0.3, 
        0.6 + n * 0.4
    ));

    // Mix colors based on noise value
    vec3 finalColor = mix(color1, color2, n);

    // Add dynamic brightness variation
    float bright = sin(iTime * 2.0) * 0.5 + 0.5;
    finalColor *= bright;

    // Add subtle motion blur effect using time offset
    vec2 prevSt = uv * (iTime - 1.0);
    float prevN = noise(prevSt * 8.0);
    finalColor += hsv2rgb(vec3(
        mod(iTime * 0.5, 1.0), 
        0.7 + sin(iTime) * 0.3, 
        0.6 + prevN * 0.4
    )) * 0.2;

    return vec4(finalColor, 1.0);
}
