vec4 Effect2(vec2 uv) {
    float t = iTime * 0.1;

    vec2 pos = uv;
    pos.x += sin(t) * 0.5;

    float noise1 = noise(pos * 8.0);
    float noise2 = noise((uv + vec2(sin(t), cos(t))) * 6.0);

    float wave = (noise1 + noise2) * 0.5;
    wave = mod(wave + t, 1.0);

    vec3 color = hsv2rgb(vec3(
        wave * 6.0,
        0.7 + sin(wave * 4.0 + t) * 0.3,
        0.8 + noise(uv * 5.0 - t) * 0.2
    ));

    return vec4(color, 1.0);
}
