vec4 Effect2(vec2 uv) {
    vec2 t = vec2(sin(iTime * 0.5), cos(iTime * 0.5));

    mat2 m = mat2(
        cos(t.x + t.y), -sin(t.x),
        sin(t.x), cos(t.y)
    );

    vec2 p = uv * 2.0 - 1.0;
    p = m * p;

    float n = noise(p * iTime);
    p += n * 0.5;

    float turb = turbulent(vec3(p, iTime * 0.5));

    vec3 color = hsv2rgb(vec3(
        (t.x + turb) * 0.5,
        0.7,
        0.5 + sin(t.y + p.x * 2.0) * 0.2
    ));

    return vec4(color, 1.0);
}
