vec4 Effect2(vec2 uv) {
    // Scale and center coordinates
    vec2 st = uv * 2.0 - 1.0;

    // Time-based animation
    float time = iTime * 0.5;

    // Grid pattern with moving squares
    vec2 grid = floor(st * 8.0 + time) / 8.0;
    vec2 squarePos = fract(grid);

    // Expanding circles from center
    vec2 circleCenter = grid - 0.5;
    float distance = length(circleCenter);
    float circle = smoothstep(0.4, 0.6, distance + time * 0.1);

    // Color transitions using HSL
    vec3 color1 = hsl(time + 0.0, 0.7, 0.5);
    vec3 color2 = hsl(time + 0.33, 0.7, 0.5);
    vec3 color3 = hsl(time + 0.66, 0.7, 0.5);

    // Mix colors based on position
    vec3 finalColor = mix(color1, color2, squarePos.x) * (1.0 - circle) +
                     mix(color2, color3, squarePos.y) * circle;

    // Add noise for texture
    float noiseValue = noise(st * 4.0 + time);
    finalColor *= 1.0 + noiseValue * 0.2;

    return vec4(finalColor, 1.0);
}
