vec4 Effect2(vec2 uv) {
    // Create animated UV coordinates
    vec2 st = uv * 5.0 + iTime;

    // Add some rotation to create movement
    float angle = iTime * 0.1;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    st = rot * st;

    // Use noise and turbulence to create a dynamic pattern
    float n = turbulent(st);
    vec3 color = hsl2rgb(vec3(
        mod(n * 0.5 + iTime, 1.0),
        0.7,
        0.6
    ));

    return vec4(color, 1.0);
}
