vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 uv_centered = uv - 0.5;

    // Calculate distance from center
    float radius = length(uv_centered);

    // Add time-based expansion
    radius += iTime * 0.1;

    // Create multiple rings with different colors
    vec3 color1 = hsl2rgb(vec3(0.0, 1.0, 0.5));   // Red
    vec3 color2 = hsl2rgb(vec3(1.0/3.0, 1.0, 0.5)); // Green
    vec3 color3 = hsl2rgb(vec3(2.0/3.0, 1.0, 0.5)); // Blue

    // Add turbulence for dynamic effect
    float turbulence = turbulent(uv_centered * 4.0 + iTime);
    radius += turbulence * 0.1;

    // Create three rings with different radii and colors
    vec3 finalColor = vec3(0.0); // Black

    if (radius < 1.0) {
        // First ring - red
        finalColor = mix(finalColor, color1, smoothstep(0.95, 1.05, radius));
    }

    if (radius > 1.2 && radius < 2.2) {
        // Second ring - green
        finalColor = mix(finalColor, color2, smoothstep(1.15, 1.25, radius));
    }

    if (radius > 2.4) {
        // Third ring - blue
        finalColor = mix(finalColor, color3, smoothstep(2.35, 2.45, radius));
    }

    return vec4(finalColor, 1.0);
}
