vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Create rotating pattern using sine functions
    vec2 st = uv * 10.0 - 5.0;
    st.x *= sin(time);
    st.y *= cos(time);

    // Add turbulence for plasma effect
    float noise1 = turbulent(st + time);
    float noise2 = turbulent(st * 0.5 + time * 0.7);

    // Mix different noise layers
    float mixNoise = (noise1 + noise2) * 0.5;

    // Create color shifts using HSL to RGB conversion
    vec3 hslColor = vec3(
        sin(time * 0.5) * 0.5 + 0.5,   // Hue variation over time
        0.7,                            // Saturation
        mixNoise * 0.5 + 0.5            // Lightness based on noise
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
