vec4 Effect2(vec2 uv) {
    // Create base radial gradient
    float d = length(uv - 0.5);

    // Add pulsing effect using sine wave
    d += sin(iTime * 0.5 + d * 10.0) * 0.3;

    // Create color shifts over time
    vec3 color = hsl(
        sin(iTime * 0.2 + d * 4.0) * 0.5 + 0.5,  // Hue variation
        0.8,                                      // Saturation
        0.5                                       // Lightness
    );

    // Add noise and turbulence for more dynamic effect
    vec2 noiseUV = uv * 10.0 + iTime;
    float n = noise(noiseUV);
    color += turbulent(vec3(uv * 4.0, iTime)) * 0.2;

    // Final color with alpha
    return vec4(color, 1.0);
}
