vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 pos = uv * 2.0 - 1.0;
    float r = length(pos);
    float a = atan(pos.y, pos.x);

    // Add time-based rotation and offset
    a += iTime * 0.5;
    pos = vec2(cos(a), sin(a)) * r;

    // Create concentric circles pattern with time modulation
    float circles = sin(r * 10.0 + iTime) * 0.5 + 0.5;
    circles *= turbulent(pos * 4.0);

    // Swirling color effect using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        (a * 0.5 + r * 0.1 + iTime) * 0.5,
        0.7,
        0.6
    ));

    // Add noise and brightness variation
    float noiseFactor = noise(pos * 4.0 + vec2(iTime)) * 0.3;
    color *= (sin(r * 10.0 - iTime) * 0.5 + 0.5 + noiseFactor);

    // Create fading trail effect based on distance from center
    float fade = smoothstep(1.0, 0.0, r);
    color *= fade;

    return vec4(color, 1.0);
}
